import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/ProductDetails.css"; // Ensure this file exists

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!id) return;

    axios
      .get(`http://localhost:8080/api/products/${id}`)
      .then((response) => {
        const cleanedData = {
          ...response.data,
          productName: response.data.productName.replace(/\?/g, " "),
        };
        setProduct(cleanedData);
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setError("Failed to load product details. Please try again.");
      });
  }, [id]);

  if (error) return <div className="error-message">{error}</div>;
  if (!product) return <div className="loading">Loading product details...</div>;

  return (
    <div className="product-detail">
      <div className="product-container">
        <div className="image-container">
          <img
            src={product.imageUrl || "https://via.placeholder.com/300"}
            alt={product.productName}
            className="product-image"
          />
        </div>
        <div className="product-info">
          <h1 className="product-name">{product.productName}</h1>
          <p className="product-category">
            Category: <span>{product.category?.categoryName || "Uncategorized"}</span>
          </p>
          <p className="product-price">â‚¹{parseFloat(product.price).toFixed(2)}</p>
          <p className="product-description">{product.description}</p>
          <div className="buttons">
            <button className="buy-now-btn">Buy Now</button>
            <button className="add-to-cart-btn">Add to Cart</button>
          </div>
          <p className="delivery-info">
            Free delivery by <strong>Tomorrow</strong> if ordered today.
          </p>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
