import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [message, setMessage] = useState("");
    const { user } = useContext(AuthContext);
    const userId = user?.userId;
    const [isMember, setIsMember] = useState(false);
    const [superCoins, setSuperCoins] = useState(0);

    useEffect(() => {
        fetchProducts();
        if (userId) {
            checkMembershipStatus();
            fetchSuperCoins();
        }
    }, [userId]);

    // Fetch all products
    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching products:", error);
            setProducts([]);
        }
    };

    // Check if the user is a member
    const checkMembershipStatus = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/users/${userId}`);
            setIsMember(response.data?.member || false);
        } catch (error) {
            console.error("Error checking membership status:", error);
        }
    };

    // Fetch user's SuperCoins balance
    const fetchSuperCoins = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/users/${userId}`);
            setSuperCoins(response.data?.superCoins || 0);
        } catch (error) {
            console.error("Error fetching SuperCoins:", error);
        }
    };

    // Add product to cart
    const addToCart = async (productId, quantity = 1) => {
        if (!userId) {
            setMessage("‚ö† Please login to add items to the cart.");
            return;
        }

        try {
            const response = await axios.post(
                `http://localhost:8080/api/cart/add`,
                null,
                { params: { userId, productId, quantity } }
            );

            if (response.status === 200) {
                const { cartId } = response.data;
                setMessage("‚úÖ Product added to cart successfully!");

                let storedCart = JSON.parse(localStorage.getItem("cart")) || [];
                storedCart.push({ cartId, productId, quantity });
                localStorage.setItem("cart", JSON.stringify(storedCart));
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            setMessage("‚ùå Failed to add product to cart.");
        }
    };

    return (
        <div className="product-container">
            <h2>Product List</h2>

            {message && <p className="alert-message">{message}</p>}

            <div className="product-grid">
                {products.length > 0 ? (
                    products.map((product, index) => {
                        const discountedPrice = (product.price * 0.9).toFixed(2);
                        const coinDiscount = Math.min(superCoins, 50);
                        const priceWithCoins = (product.price - coinDiscount).toFixed(2);

                        return (
                            <div className="product-card" key={product.productId || index}>
                                <img
                                    src={product.productUrl || "https://via.placeholder.com/150"}
                                    alt={product.name || "Product Image"}
                                    className="product-image"
                                />
                                <div className="product-info">
                                    <h3>{product.name}</h3>
                                    <p className="product-description">{product.description}</p>

                                    <p className="product-price">
                                        {isMember ? (
                                            <>
                                                <span className="price-label">Original Price: </span>
                                                <span className="strikethrough">‚Çπ{product.price}</span>
                                                <br />
                                                <span className="price-label">Discounted Price: </span>
                                                <span className="discount-price">‚Çπ{discountedPrice}</span>
                                            </>
                                        ) : (
                                            <>
                                                <span className="price-label">Original Price: </span>
                                                <span className="strikethrough">‚Çπ{product.price}</span>
                                                <br />
                                                <span className="price-label">Discounted Price: </span>
                                                <span className="discount-price">‚Çπ{discountedPrice}</span>
                                                <span className="non-member-text"> (For Members Only)</span>
                                            </>
                                        )}
                                    </p>

                                    {superCoins > 0 && (
                                        <p className="supercoins-offer">
                                            <span>üî• Buy with </span>
                                            <b>{coinDiscount} SuperCoins</b> + <b>‚Çπ{priceWithCoins}</b>
                                        </p>
                                    )}

                                    <div className="product-buttons">
                                        <button className="add-to-cart" onClick={() => addToCart(product.productId, 2)}>
                                            Add to Cart
                                        </button>
                                        <button className="buy-now">Buy Now</button>
                                    </div>
                                </div>
                            </div>
                        );
                    })
                ) : (
                    <p className="no-products">No products found</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
