import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "../styles/subSubcategories.css";

const SubSubcategoryPage = () => {
  const { subcategoryId } = useParams();
  const [subSubcategories, setSubSubcategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSubSubcategories = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/subsubcategories/${subcategoryId}`
        );
        setSubSubcategories(response.data);
      } catch (err) {
        setError("Error fetching sub-subcategories");
      } finally {
        setLoading(false);
      }
    };

    fetchSubSubcategories();
  }, [subcategoryId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="subsubcategory-container">
      <h2 className="subsubcategory-title">Sub-Subcategories</h2>
      <div className="subsubcategory-grid">
        {subSubcategories.map((subsubcategory) => (
          <Link
            key={subsubcategory.id}
            to={`/productsbycategory/${subsubcategory.name}`}
            className="subsubcategory-card"
          >
            <img
              src={subsubcategory.imageUrl || "https://via.placeholder.com/150"}
              alt={subsubcategory.name}
            />
            <h3>{subsubcategory.name}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SubSubcategoryPage;
