import React, { useContext, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import { PersonCircle, Cart3, List } from "react-bootstrap-icons";
import "../styles/Header.css";
import AuthContext from "../AuthContext";
import axios from "axios";

const Header = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const userId = user?.userId;

  const [cartCount, setCartCount] = useState(0);
  const [userData, setUserData] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    if (!userId) return;
    fetchCartCount();
    fetchUserData();
  }, [userId]);

  // Fetch Cart Count
  const fetchCartCount = async () => {
    try {
      console.log("Fetching cart data for user ID:", userId);
      const response = await axios.get(`http://localhost:8080/api/cart/user/${userId}`);
      console.log("Cart Data Response:", response.data); // Debug Log
      setCartCount(response.data.length);
    } catch (error) {
      console.error("Error fetching cart count:", error);
    }
  };

  // Fetch User Data (Name & Supercoins)
  const fetchUserData = async () => {
    try {
      console.log("Fetching user data for user ID:", userId);
      const response = await axios.get(`http://localhost:8080/users/${userId}`);
      console.log("User Data Response:", response.data); // Debug Log
      setUserData(response.data);
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  {/* Language Dropdown */}
            <select className="form-select w-auto me-3" value={language} onChange={(e) => setLanguage(e.target.value)}>
              <option value="en">English</option>
              <option value="hi">à¤¹à¤¿à¤‚à¤¦à¥€</option>
              <option value="mr">à¤®à¤°à¤¾à¤ à¥€</option>
            </select>

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg fixed-top shadow-sm">
        <div className="container-fluid">
          {/* Logo */}
          <Link className="navbar-brand" to="/dashboard">
            <img
              src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSF8ExH1zQPwZ4z3pa4gdCl76l9eJ4T7Ai8iw&s"
              alt="E-Mart Logo"
              className="logo-img"
            />
          </Link>

          {/* Toggle Button for Small Screens */}
          <button
            className="navbar-toggler"
            type="button"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            <List size={25} />
          </button>

          {/* Navbar Links */}
          <div className={`collapse navbar-collapse ${isMenuOpen ? "show" : ""}`} id="navbarNav">
            <ul className="navbar-nav mx-auto">
              <li className="nav-item"><Link className="nav-link" to="/dashboard">Home</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/productlist">Products</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/categorylist">Categories</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/orders">Orders</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/contact">Contact</Link></li>
            </ul>

            {/* User Section (Desktop) */}
            <div className="auth-section d-flex align-items-center">
              {/* Cart Icon */}
              <Link to="/cart" className="cart-icon">
                <Cart3 size={22} />
                {cartCount > 0 && <span className="cart-badge">{cartCount}</span>}
              </Link>

              {user ? (
                <div className="d-flex align-items-center">
                  {/* Display Username and Supercoins on Desktop */}
                  <span className="user-info d-none d-lg-block">
                    {userData?.name} | <span className="supercoins">{userData?.superCoins} ðŸª™</span>
                  </span>

                  {/* Profile Icon */}
                  <Link to="/profile" className="profile-icon">
                    <PersonCircle size={20} />
                  </Link>

                  {/* Logout Button */}
                  <button className="btn btn-danger fw-bold ms-2" onClick={handleLogout}>Logout</button>
                </div>
              ) : (
                <button className="btn btn-light fw-bold ms-2" onClick={() => navigate("/login")}>Login</button>
              )}
            </div>

            {/* User Section (Mobile) */}
            {user && (
              <div className="mobile-user-info d-lg-none">
                <p>{userData?.name} | <span className="supercoins">{userData?.superCoins} ðŸª™</span></p>
                <Link to="/profile" className="btn btn-light btn-sm w-100">Profile</Link>
                <button className="btn btn-danger btn-sm w-100 mt-2" onClick={handleLogout}>Logout</button>
              </div>
            )}
          </div>
        </div>
      </nav>

      {/* Ensure Page Content Starts Below Header */}
      <div className="main-content"></div>
    </>
  );
};

export default Header;
