import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/SubSubCategories.css";
import "bootstrap/dist/css/bootstrap.min.css";

const SubSubcategories = () => {
  const { subcategoryId } = useParams();
  const [subSubcategory, setSubSubcategory] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!subcategoryId) {
      setError("Invalid Subcategory ID");
      setLoading(false);
      return;
    }

    const fetchSubSubcategory = async () => {
      try {
        console.log(`Fetching data from: http://localhost:8080/api/subsubcategories/${subcategoryId}`);
        const response = await axios.get(
          `http://localhost:8080/api/subsubcategories/${subcategoryId}`
        );

        console.log("API Response Data:", response.data);

        // Store response as an object (API returns a single sub-subcategory)
        setSubSubcategory(response.data);
      } catch (err) {
        console.error("API Fetch Error:", err);
        setError("Error fetching sub-subcategory");
      } finally {
        setLoading(false);
      }
    };

    fetchSubSubcategory();
  }, [subcategoryId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!subSubcategory) return <div className="text-center">No sub-subcategory found.</div>;

  return (
    <div className="container">
      <p> {subSubcategory.subcategory?.category?.categoryName}</p>
     
      <div className="subsubcategories-card">
        <img 
          src={subSubcategory.imageUrl || "https://via.placeholder.com/250"} 
          alt={subSubcategory.name} 
          className="subsubcategories-image"
        />
        <div className="subsubcategories-details">
          <p>{subSubcategory.subcategory?.subcategoryName}</p>
          <p className="text-center my-4">{subSubcategory.name}</p> {/* Sub-Subcategory Name */}
      
          
        </div>
      </div>
    </div>
  );
};

export default SubSubcategories;
