import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Carousel } from "react-bootstrap";
import Header from "./Header";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  return (
    <div>
      {isLoading && (
        <div className="preloader">
          <div className="spinner"></div>
        </div>
      )}

      <Header />

      {/* Hero Section with Flipkart-Style Carousel */}
      <div className="hero-section">
        <Carousel controls={true} indicators={true} interval={3000} pause={false} fade>
          <Carousel.Item>
            <img
              src="https://source.unsplash.com/1600x600/?shopping,ecommerce"
              className="d-block w-100"
              alt="Shopping Banner 1"
            />
            <Carousel.Caption>
              <h3>Exclusive Deals on Electronics</h3>
              <p>Grab the latest gadgets at unbeatable prices!</p>
              <a href="/electronics" className="btn btn-warning">Shop Now</a>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              src="https://source.unsplash.com/1600x600/?fashion,sale"
              className="d-block w-100"
              alt="Shopping Banner 2"
            />
            <Carousel.Caption>
              <h3>Fashion Sale - Up to 70% Off</h3>
              <p>Upgrade your wardrobe with trending styles.</p>
              <a href="/fashion" className="btn btn-primary">Shop Now</a>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
              src="https://source.unsplash.com/1600x600/?electronics,gadgets"
              className="d-block w-100"
              alt="Shopping Banner 3"
            />
            <Carousel.Caption>
              <h3>Best Smartphones of 2025</h3>
              <p>Find the perfect phone for you at the best price.</p>
              <a href="/smartphones" className="btn btn-success">Shop Now</a>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
      </div>

      {/* Welcome Section */}
      <div className="container my-5 text-center">
        <h2>Welcome to E-Mart</h2>
        <p>Discover the latest trends in fashion, electronics, and more!</p>
        <a href="/productpage" className="btn btn-primary btn-lg">
          Start Shopping
        </a>
      </div>
    </div>
  );
};

export default Dashboard;
