import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "../styles/subcategoryPage.css"; 

const SubcategoryPage = () => {
  const { categoryId } = useParams();
  const [subcategories, setSubcategories] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:8080/api/subcategories/category/${categoryId}`)
      .then(response => setSubcategories(response.data))
      .catch(error => console.error("Error fetching subcategories:", error));
  }, [categoryId]);

  return (
    <div className="subcategory-container">
      <h2>Subcategories for Category {categoryId}</h2>
      <div className="subcategory-grid">
        {subcategories.map((subcategory) => (
          <Link 
            key={subcategory.subcategoryId} 
            to={`/subsubcategories/${subcategory.subcategoryId}`} 
            className="subcategory-card"
          >
            <img src={subcategory.imageUrl || "default-image.jpg"} alt={subcategory.subcategoryName} />
            <h3>{subcategory.subcategoryName}</h3>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SubcategoryPage;
