import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [message, setMessage] = useState("");
    const { user } = useContext(AuthContext);
    const userId = user?.userId;
    const [isMember, setIsMember] = useState(false);
    const [superCoins, setSuperCoins] = useState(0);
    const [selectedCoins, setSelectedCoins] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        fetchProducts();
        if (userId) {
            checkMembershipStatus();
            fetchSuperCoins();
        }
    }, [userId]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            setProducts([]);
        }
    };

    const checkMembershipStatus = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/users/${userId}`);
            setIsMember(response.data?.member || false);
        } catch (error) {}
    };

    const fetchSuperCoins = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/users/${userId}`);
            setSuperCoins(response.data?.superCoins || 0);
        } catch (error) {}
    };

    const handleSuperCoinToggle = (productId) => {
        setSelectedCoins((prev) => ({
            ...prev,
            [productId]: !prev[productId],
        }));
    };

    return (
        <div className="product-container">
            <table className="product-table">
                <thead>
                    <tr>
                        <th className="product-header"><center><h1>Products</h1></center></th>
                    </tr>
                </thead>
                <tbody>
                    {message && <p className="alert-message">{message}</p>}
                    {products.length > 0 ? (
                        products.reduce((rows, product, index) => {
                            if (index % 2 === 0) rows.push([]);
                            rows[rows.length - 1].push(product);
                            return rows;
                        }, []).map((row, rowIndex) => (
                            <tr key={rowIndex}>
                                {row.map((product) => {
                                    const discountedPrice = (product.price * 0.9).toFixed(2);
                                    const coinDiscount = selectedCoins[product.productId] ? Math.min(superCoins, 100) : 0;
                                    const priceWithCoins = (product.price - (coinDiscount / 10)).toFixed(2);
                                    const isChecked = selectedCoins[product.productId];

                                    return (
                                        <td key={product.productId} className="product-card">
                                            <img
                                                src={product.producturl || "https://via.placeholder.com/150"}
                                                alt={product.name || "Product Image"}
                                                className="product-image"
                                            />
                                            <div className="product-info">
                                                <h3>{product.productName}</h3>
                                                <p className="product-description">{product.description}</p>
                                                <p className="product-price">
                                                    {isMember ? (
                                                        <>
                                                            <span className={isChecked ? "strikethrough" : "original-price"}>â‚¹{product.price}</span>
                                                            <br />
                                                            <span className={isChecked ? "strikethrough" : "discount-price"}>â‚¹{discountedPrice}</span>
                                                        </>
                                                    ) : (
                                                        <>
                                                            <span className={isChecked ? "strikethrough" : "original-price"}>â‚¹{product.price}</span>
                                                            <br />
                                                            <span className="strikethrough"> â‚¹{discountedPrice}</span>
                                                            <br />
                                                            <span className="non-member-text">(For Members Only)</span>
                                                        </>
                                                    )}
                                                </p>
                                                <input
                                                    type="checkbox"
                                                    checked={isChecked}
                                                    onChange={() => handleSuperCoinToggle(product.productId)}
                                                /> Use 100 SuperCoins for â‚¹10 off
                                                {superCoins > 0 && (
                                                    <p className="supercoins-offer">
                                                        ðŸ”¥ Buy with <b>{coinDiscount} SuperCoins</b> + <b>â‚¹{priceWithCoins}</b>
                                                    </p>
                                                )}
                                            </div>
                                        </td>
                                    );
                                })}
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="2" className="no-products">No products found</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ProductList;
