import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "../styles/CategoryList.css";

const SubSubcategoryPage = () => {
  const { subcategoryId } = useParams();
  const [subSubcategories, setSubSubcategories] = useState([]); // Ensure it starts as an array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSubSubcategories = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/subsubcategories/subcategory/${subcategoryname}`
        );

        // Ensure we set an array, even if the response is not what we expect
        setSubSubcategories(response.data || []);
      } catch (err) {
        setError("Error fetching sub-subcategories");
      } finally {
        setLoading(false);
      }
    };

    fetchSubSubcategories();
  }, [subcategoryId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  // Ensure `subSubcategories` is always treated as an array
  return (
    <div className="category-container">
      <h2 className="category-title">Sub-Subcategories</h2>
      <div className="category-grid">
        {Array.isArray(subSubcategories) && subSubcategories.length > 0 ? (
          subSubcategories.map((subSubcategory) => (
            <Link
              key={subSubcategory.id}
              to={`/productbycategory/${subSubcategory.id}`}
              className="category-card"
            >
              <img
                src={subSubcategory.imageUrl || "https://via.placeholder.com/150"}
                alt={subSubcategory.name}
              />
              <h3>{subSubcategory.name}</h3>
            </Link>
          ))
        ) : (
          <p className="no-data">No sub-subcategories available</p>
        )}
      </div>
    </div>
  );
};

export default SubSubcategoryPage;
