import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Header.css";
import AuthContext from "../AuthContext";

const Header = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top shadow-lg">
      <div className="container-fluid">
        {/* Logo */}
        <Link className="navbar-brand fw-bold fs-3 logo" to="/dashboard">
          E-Mart
        </Link>

        {/* Navbar Toggle for Mobile */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse justify-content-between" id="navbarNav">
          {/* Left Section - Links */}
          <ul className="navbar-nav mx-auto">
            <li className="nav-item">
              <Link className="nav-link fs-5" to="/dashboard">Home</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link fs-5" to="/productpage">Products</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link fs-5" to="/categorylist">Categories</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link fs-5" to="/orders">Orders</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link fs-5" to="/contact">Contact</Link>
            </li>
          </ul>

          {/* Right Section - Auth Buttons */}
          <div className="auth-section">
            {user ? (
              <div className="d-flex align-items-center">
                <span className="text-light me-3 fw-bold">{user.email}</span>
                <button className="btn btn-danger fw-bold" onClick={handleLogout}>
                  Logout
                </button>
              </div>
            ) : (
              <button className="btn btn-light fw-bold" onClick={() => navigate("/login")}>
                Login
              </button>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
