import React, { useEffect, useState } from "react";
import "../styles/Banner.css";

const Banner = () => {
  const slides = [
    { 
      image: "https://img.paisawapas.com/ovz3vew9pw/2022/09/21231652/Frame-2029-1.jpg", 
      text: "Mega Discounts on Latest Fashion!", 
      subtext: "Up to 70% off on top brands", 
      button: "Shop Now" 
    },
    { 
      image: "https://img.freepik.com/free-vector/hand-drawn-electronics-store-sale-banner-template_23-2151138129.jpg", 
      text: "Exclusive Deals on Electronics!", 
      subtext: "Top gadgets at unbeatable prices", 
      button: "Explore Now" 
    },
    { 
      image: "https://img.freepik.com/premium-vector/new-arrival-seasonal-shoes-sale-social-media-web-banner_620267-120.jpg?semt=ais_incoming", 
      text: "New Arrivals in Footwear!", 
      subtext: "Trendy styles, best comfort", 
      button: "Grab Yours" 
    },
  ];

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <div className="banner">
      <div className="slider-container">
        <div
          className="slide-images"
          style={{
            transform: `translateX(-${currentSlide * 100}%)`,
            transition: "transform 1s ease-in-out",
          }}
        >
          {slides.map((slide, index) => (
            <div className="slide" key={index}>
              <img src={slide.image} alt={`Slide ${index + 1}`} className="slide-img" />
            </div>
          ))}
        </div>
      </div>

      <div className="banner-content">
        <h1>{slides[currentSlide].text}</h1>
        <p>{slides[currentSlide].subtext}</p>
        <button className="banner-btn">{slides[currentSlide].button}</button>
      </div>
    </div>
  );
};

export default Banner;
