import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/ProductList.css";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [category, setCategory] = useState("");
    const [subcategory, setSubcategory] = useState("");
    const [productName, setProductName] = useState("");

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            if (Array.isArray(response.data)) {
                setProducts(response.data);
            } else {
                console.error("API response is not an array:", response.data);
                setProducts([]);
            }
        } catch (error) {
            console.error("Error fetching products:", error);
            setProducts([]);
        }
    };

    const fetchByCategory = async () => {
        if (!category.trim()) return;
        try {
            const response = await axios.get(`http://localhost:8080/api/products/category/${category}`);
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching by category:", error);
            setProducts([]);
        }
    };

    const fetchBySubcategory = async () => {
        if (!subcategory.trim()) return;
        try {
            const response = await axios.get(`http://localhost:8080/api/products/subcategory/${subcategory}`);
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching by subcategory:", error);
            setProducts([]);
        }
    };

    const fetchByProductName = async () => {
        if (!productName.trim()) return;
        try {
            const response = await axios.get(`http://localhost:8080/api/products/name/${productName}`);
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching by name:", error);
            setProducts([]);
        }
    };

    return (
        <div className="product-container">
            <h2>Product List</h2>

            <div className="filters">
                <input
                    type="text"
                    placeholder="Filter by Category"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                />
                <button onClick={fetchByCategory}>Search</button>

                <input
                    type="text"
                    placeholder="Filter by Subcategory"
                    value={subcategory}
                    onChange={(e) => setSubcategory(e.target.value)}
                />
                <button onClick={fetchBySubcategory}>Search</button>

                <input
                    type="text"
                    placeholder="Filter by Product Name"
                    value={productName}
                    onChange={(e) => setProductName(e.target.value)}
                />
                <button onClick={fetchByProductName}>Search</button>
            </div>

            <div className="product-grid">
                {products.length > 0 ? (
                    products.map((product) => (
                        <div className="product-card" key={product.id || product.product_id}>
                            <img
                                src={product.image || "https://via.placeholder.com/150"}
                                alt={product.name || "Product Image"}
                                className="product-image"
                            />
                            <div className="product-info">
                                <h3>{product.name || product.product_name}</h3>
                                <p className="product-description">{product.description}</p>
                                <p className="product-price">${product.price}</p>
                                <div className="product-buttons">
                                    <button className="add-to-cart">Add to Cart</button>
                                    <button className="buy-now">Buy Now</button>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="no-products">No products found</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
