import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "../styles/CategoryList.css";

const SubSubcategoryPage = () => {
  const { subcategoryId } = useParams();
  const [subSubcategories, setSubSubcategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // ... (API call remains the same)
  }, [subcategoryId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="category-container">
      <h2 className="category-title">Sub-Subcategories</h2>
      <div className="category-grid">
        {Array.isArray(subSubcategories) && subSubcategories.length > 0 ? (
          subSubcategories.map((subSubcategory) => (
            <Link
              key={subSubcategory.id} // Access subcategory.id directly
              to={`/productbycategory/${subSubcategory.id}`}
              className="category-card"
            >
              <img
                src={subSubcategory.images ? subSubcategory.images[0] : "https://via.placeholder.com/150"} // Handle images array
                alt={subSubcategory.name} // Access subcategory.name directly
              />
              <h3>{subSubcategory.name}</h3>  {/* Access subcategory.name directly */}
            </Link>
          ))
        ) : (
          <p className="no-data">No sub-subcategories available</p>
        )}
      </div>
    </div>
  );
};

export default SubSubcategoryPage;
