import React, { useEffect, useState } from "react";
import axios from "axios";

const PaymentPage = ({ userId, orderId }) => {
    const [user, setUser] = useState(null);
    const [orderDetails, setOrderDetails] = useState(null);
    const [product, setProduct] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch User Details
                const userResponse = await axios.get(`http://localhost:8080/users/${userId}`);
                setUser(userResponse.data);
                console.log("User Data:", userResponse.data);

                // Fetch Order Details
                const orderResponse = await axios.get(`http://localhost:8080/api/orderdetails/order/${orderId}`);
                setOrderDetails(orderResponse.data);
                console.log("Order Details:", orderResponse.data);

                // Ensure order response has products
                if (!orderResponse.data || orderResponse.data.length === 0) {
                    console.error("Order details are empty.");
                    return;
                }

                // Extract the first product ID from the order
                const productId = orderResponse.data[0].productId; 
                console.log("Extracted Product ID:", productId);

                if (!productId) {
                    console.error("Product ID is undefined.");
                    return;
                }

                // Fetch Product Details
                const productResponse = await axios.get(`http://localhost:8080/api/products/${productId}`);
                setProduct(productResponse.data);
                console.log("Product Data:", productResponse.data);

            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, [userId, orderId]);

    return (
        <div>
            <h2>Payment Page</h2>
            {user && (
                <div>
                    <p>Name: {user.name}</p>
                    <p>Email: {user.email}</p>
                    <p>Mobile: {user.mobile}</p>
                    <p>Address: {user.address}</p>
                </div>
            )}

            {product && (
                <div>
                    <h3>Product Details</h3>
                    <p>Name: {product.name}</p>
                    <p>Price: â‚¹{product.price}</p>
                    <p>Description: {product.description}</p>
                    <img src={product.image} alt={product.name} width="200" />
                </div>
            )}
        </div>
    );
};

export default PaymentPage;
