import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Orders.css";
import AuthContext from "../AuthContext"; // Import AuthContext

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const { userId, token } = useContext(AuthContext); // Get logged-in user's ID and token
  const navigate = useNavigate();

  useEffect(() => {
    if (!userId) {
      console.warn("No user is logged in.");
      return;
    }

    const fetchOrders = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/orders/${userId}`, {
          headers: { Authorization: `Bearer ${token}` }, // Include token if needed
        });

        setOrders(response.data || []); // Ensure orders list is always an array
      } catch (error) {
        console.error("Error fetching orders:", error);
        setOrders([]); // Reset orders on error
      }
    };

    fetchOrders();
  }, [userId, token]); // Refetch when userId or token changes

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">Your Orders</h2>

      {orders.length === 0 ? (
        <p className="text-center">No orders found.</p>
      ) : (
        <ul className="order-list">
          {orders.map((order) => (
            <li key={order.orderId} className="order-item">
              <span>Order ID: #{order.orderId}</span>
              <span>Date: {new Date(order.orderDate).toLocaleString()}</span>
              <span>Total: ${order.totalAmount.toFixed(2)}</span>
              <button
                className="btn btn-primary btn-sm"
                onClick={() => navigate(`/order-details/${order.orderId}`)}
              >
                View Details
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Orders;
