import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Footer.css"; // Import the CSS file

const Footer = () => {
  const [location, setLocation] = useState("Fetching location...");
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString());

  // Fetch user location using API
  useEffect(() => {
    fetch("https://ipapi.co/json/")
      .then((response) => response.json())
      .then((data) => setLocation(`${data.city}, ${data.country_name}`))
      .catch(() => setLocation("Location not available"));
  }, []);

  // Update time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-content">
          <p>&copy; 2025 Unstoppable GRP3. All rights reserved.</p>
          <p>Contact: support@emart.com | Toll-Free: 1800-123-4567</p>
          <p>Address: 123 E-Mart Street, Commerce City, USA</p>
          <p>üìç Location: {location} | üïí Time: {currentTime}</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
