import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css";

const Navbar = () => {
  const [location, setLocation] = useState("Fetching location...");

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const response = await fetch(
              `https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}`
            );
            const data = await response.json();

            console.log("Location Data:", data); // Debugging to check available fields

            const city = data.address.city || data.address.town || data.address.village || data.address.hamlet || "Unknown City";
            const state = data.address.state || data.address.county || data.address.region || data.address.province || data.address.state_district || "Unknown State";
            const country = data.address.country || "Unknown Country";

            setLocation(`${city}, ${country}`);
          } catch (error) {
            console.error("Error fetching location:", error);
            setLocation("Location unavailable");
          }
        },
        (error) => {
          console.error("Geolocation error:", error);
          setLocation("Permission denied");
        }
      );
    } else {
      setLocation("Geolocation not supported");
    }
  }, []);

  return (
    <nav className="navbar">
      {/* Animated Logo */}
      <div className="logo-container">
        <span className="logo">eMart</span>
      </div>

      {/* User Location */}
      <div className="location">
        <div className="location-icon">üìç</div>
        <span>Deliver to {location}</span>
      </div>

      {/* Search Bar */}
      <div className="search-container">
        <input type="text" placeholder="Search for products..." className="search-bar" />
        <button className="search-button">üîç</button>
      </div>

      {/* User Profile Links */}
      <div className="nav-links">
        <Link to="/profile">Your Profile</Link>
        <Link to="/orders">Your Orders</Link>
        <Link to="/signout">Sign Out</Link>
      </div>

      {/* Cart */}
      <div className="cart">
        <div className="cart-icon">üõí</div>
        <Link to="/cart" className="cart-text">Cart</Link>
      </div>
    </nav>
  );
};

export default Navbar;
